import "./user-card.scss";
import userImg from "../images/mika.jpg";

const userCard = () => {
  return (
    <div className="user-card">
      <div className="user-card__info">
        <img className="user-card__img" src={userImg} alt="Mika" />
        <div className="user-card__user">
          <h3 className="user-card__name">Mika</h3>
          <span className="user-card__age">14</span>
        </div>
        <p className="user-card__location">Osh</p>

        <div className="user-card__stat">
          <div className="user-card__stat-inner">
            <div className="user-card__stat-item">
              <p className="user-card__stat-count">75K</p>
              <span className="user-card__stat-descr">Followers</span>
            </div>
            <div className="user-card__stat-item">
              <p className="user-card__stat-count">725K</p>
              <span className="user-card__stat-descr">Likes</span>
            </div>
            <div className="user-card__stat-item">
              <p className="user-card__stat-count">1.0K</p>
              <span className="user-card__stat-descr">Photos</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default userCard;


